mapper.select:
select generalize_id,g.generalize_address address_type,generalize_money,generalize_commission,generalize_start_time,generalize_end_time,brand_id from system_generalize s inner join generalize_address g on s.address_id=g.address_id where 1=1
and s.address_id=#{assessid}
limit #{page},#{limit}

  		select count(1) from system_generalize
  		where 1=1

		and address_id=#{address}
