mapper.select:
	 select 

  from template
  where 1=1 and brand_id is null

   and   discount_template_id=#{discountTemplateId}


   and   discounts_start_time=#{discountsStartTime}


   and   discounts_end_time = #{discountsEndTime} 

    limit #{page},#{pageSize}

	select count(*) from template 
	where 1=1 and brand_id is null

   and   discount_template_id=#{discountTemplateId}


   and   discounts_start_time=#{discountsStartTime}


   and   discounts_end_time = #{discountsEndTime}

	select count(*) from template 
	where 1=1 and brand_id=#{brandId}

   and   discount_template_id=#{discountTemplateId}


   and   discounts_start_time=#{discountsStartTime}


   and   discounts_end_time = #{discountsEndTime}

	 select 

  from template
  where 1=1 and brand_id=#{brandId}

   and   discount_template_id=#{discountTemplateId}


   and   discounts_start_time=#{discountsStartTime}


   and   discounts_end_time = #{discountsEndTime} 

    limit #{page},#{pageSize}
